补全信息方法：
bool Job::SetDetail(int id, const std::string &duties, const std::string &requirements)
{
    Job* p_job = _job_container.get_pointer(id);
    if (p_job == nullptr) return false;
    return SetDetail(*p_job, duties, requirements);
}

bool Job::SetDetail(Job& job, const std::string &duties, const std::string &requirements)
{
    job.m_duties = duties;
    job.m_requirements = requirements;
    job.m_version = Common::DateHelper::CurrentTimeMillis();
    _job_container.notify_job_full(job);//将状态置为“信息已完整”
    _job_available_callback(job);//回调方法告知Job可用

    using namespace Base::Database;
    static const std::string kSql("UPDATE tjh_job_detail SET job_duties=?,job_requirements=?,version=?,is_full=? WHERE job_id=?;");
    Common::DBManager::EnqueueDMLTask(SqlTaskPriority::Normal, [](const DMLSqlTask& task){
        if (task.execsql_succeeded() && task.get_result() == 0)
        {
            G::LogOfDatabase().errorAndNotifyAdmin(
                fmt("执行修改语句时数据库无变动，数据有可能被人为修改了，语句[%1%]") % task.to_string()
                );
        }
    }, kSql,
        job.m_duties, job.m_requirements,job.m_version, job.m_is_full, job.m_id);
    return true;
}

设置职位失效/有效的方法
void Job::SetIsAvailable(int job_id, bool state)
{
    Job* p_job = _job_container.get_pointer(job_id);
    SetIsAvailable(p_job,state);
}

void Job::SetIsAvailable(Job* p_job, bool state)
{
    using namespace Base::Database;
    if (p_job != nullptr)
    {
        p_job->m_is_available = false;

        static const std::string kSqlJobDetailSetAvailable("UPDATE tjh_job_detail SET is_available = ? WHERE job_id = ?;");
        Common::DBManager::EnqueueDMLTask(SqlTaskPriority::Normal, kSqlJobDetailSetAvailable , state, p_job->m_id);
    }
}

int Job::SetUnavailableDiffernce(const std::set& job_id_set)
{
    using namespace Base::Database;
    //先求差集
    std::list exclude_list;
    _job_container.get_difference(job_id_set, exclude_list);

    static const std::string kSqlJobDetailSetUnavailable("UPDATE tjh_job_detail SET is_available = false WHERE job_id = ?;");
    for (Job* p_job : exclude_list)
    {
        p_job->m_is_available = false;
        Common::DBManager::EnqueueDMLTask(SqlTaskPriority::Normal, kSqlJobDetailSetUnavailable, p_job->m_id);
    }

    return exclude_list.size();
}

查询方法：
std::shared_ptr Job::Query(const std::string& query_exp_str, std::vector& result)
{
    auto exp = JobDetailQueryExpressionHelper::Parse(query_exp_str);
    ForeachJobReadonly([&exp, &result](const Model::Job& job){
        if (job.get_is_available() && exp->is_match(job))result.emplace_back(&job);
    }, 0);
    return exp;
}

uint64_t Job::Query(const std::shared_ptr& query_exp,
    std::vector& result, uint64_t version_after)
{
    return ForeachJobReadonly([&query_exp, &result](const Model::Job& job){
        if (job.get_is_available() && query_exp->is_match(job))result.emplace_back(&job);
    }, version_after);
}

表达式对象自带is_match匹配方法可以判断job是否满足表达式的要求，关于表达式编译器的实现请看查询表达式编译器章节
初始化方法（从数据库加载数据到容器）：
void Job::AsyncLoadFromDataBase()
{
    using Common::DateHelper;
    using namespace Base::Database;

    //读取职位类别数据（id-名称对）
    static const std::string kSqlQueryJobType("SELECT type_id,type_name FROM tjh_job_type;");
    Common::DBManager::EnqueueDQLTask(SqlTaskPriority::Highest, [](const DQLSqlTask& task){
        foreach_line_in_resultset_init(task, [](sql::ResultSet* res){
            auto it = _type_map_r.emplace(res->getString("type_name"), res->getInt("type_id")).first;
            _type_map.emplace(it->second, it->first);
            _type_count++;
        });
    }, kSqlQueryJobType);

    //读取职位地点数据（id-名称对）
    static const std::string kSqlQueryJobLocation("SELECT location_id,location_name FROM tjh_job_location;");
    Common::DBManager::EnqueueDQLTask(SqlTaskPriority::Highest, [](const DQLSqlTask& task){
        foreach_line_in_resultset_init(task, [](sql::ResultSet* res){
            auto it = _location_map_r.emplace(res->getString("location_name"), res->getInt("location_id")).first;
            _location_map.emplace(it->second, it->first);
            _location_count++;
        });
    }, kSqlQueryJobLocation);

    //读取职位数据
    static const std::string kSqlQueryJobDetail("SELECT * FROM tjh_job_detail;");
    Common::DBManager::EnqueueDQLTask(SqlTaskPriority::Highest, [](const DQLSqlTask& task){
        foreach_line_in_resultset_init(task, [](sql::ResultSet* res){
            uint64_t version = res->getUInt64("version");
            _job_container.emplace(
                res->getInt("job_id"), res->getString("job_title").c_str(), res->getInt("job_type"), res->getInt("job_hiring_number"),
                res->getInt("job_location"), DateHelper::ToDate(res->getString("job_date").c_str()),
                res->getString("job_duties").c_str(), res->getString("job_requirements").c_str(),
                version, res->getBoolean("is_full"),res->getBoolean("is_available"));
        });
    }, kSqlQueryJobDetail);

    //加载职位已经抓取完整的日期(爬虫模块进行抓取作业时将跳过这些日期)
    static const std::string kSqlQueryJobDateOfFull("SELECT job_date FROM tjh_job_date_of_full;");
    Common::DBManager::EnqueueDQLTask(SqlTaskPriority::Highest, [](const DQLSqlTask& task){
        foreach_line_in_resultset_init(task, [](sql::ResultSet* res){
            _date_set_of_job_info_full.emplace(DateHelper::ToInt(DateHelper::ToDate(res->getString("job_date").c_str())));
        });

        //填充用于传输职位地点、类型等信息的protobuf数据
        for (auto it = _type_map.begin(); it != _type_map.end(); it++) {
            _job_related_info.add_type_id(it->first);
            _job_related_info.add_type_name(it->second);
        }
        for (auto it = _location_map.begin(); it != _location_map.end(); it++) {
            _job_related_info.add_location_id(it->first);
            _job_related_info.add_location_name(it->second);
        }

        _is_ready = true;
        _cond_ready.notify_one();//通知主线程整个查询任务已经结束
        G::LogOfProgram().info("Job库已完成初始化");
    }, kSqlQueryJobDateOfFull);

}

logic/server/model/queryresult.html.body:
查询结果类
由于查询结果数目可能很多，服务器一次性将结果全部返回给客户端信息量太大，对双方都是一种负担 。
本项目采用的解决方法是将查询结果直接存储下来并分页，每次客户端请求时只返回部分查询结果。但限制每个用户同时允许持有的查询结果实例，每个用户最多同时进行3个查询。
本类存储查询结果的相关信息，包括查询结果的id、查询结果里的职位指针等等。
具体属性和方法如下：
实例属性：



属性名称
类型
描述




id
std::string
每个查询结果创建时随机生成的字符串。通过这个id来定位查询结果的实例


query_no
int
每个用户最多同时拥有3个查询，再多的话就只能删除旧查询后再创建新的。这个属性表示它所占有的是用户的哪个查询，其取值范围为0~2


query_expression_str
std::string
创建查询时所使用的查询表达式


version
uint64_t
进行查询的那一刻，职位库的版本（最后一次创建职位的时间）。 由于查询任务是在现有查询结果的基础上创建的，这个值将会成为新旧职位的分界线。一个职位的版本(创建时间)比它新的话，会被视为是新职位，反之视为旧职位。


user
User&
所持有的用户的引用，表示它是由哪位用户发起的查询结果。


datas
std::vector
查询结果，类型为职位类实例指针所组成的数组



实例方法（针对某个具体查询结果的操作）：



实例方法
描述




各属性的get方法
如get_id()、get_query_no()等



类方法（针对整个查询结果库的操作）：



类方法
描述




static JobQueryResult& Add(User& user, int query_no,const std::string& query_expression_str, const std::vector& datas, uint64_t version)
查询完成后，将查询结果加入到查询结果库中


static JobQueryResult* GetPointer(const std::string& id)
通过id获取查询结果


static bool ContainsKey(const std::string& id)
判断是否存在该id对应的查询结果


static void Erase(const std::string& id)
删除查询结果


static bool Use(const std::string& id, const std::function& func)
通过id，使用某个具体的查询结果实例，执行过程中可以保证该实例绝对安全。若id不存在则返回false。



这里和职位类相比多了个特殊的方法Use，这是因为跟职位类不提供删除(Erase)方法不同，查询结果随时有可能会被删除。因此通过GetPointer()获得的指针其实是不安全的，如果实例被删除，那个指针会变成野指针，进而可能引发不可预知的后果。
因此引入这个方法，它能够保证执行Use()的过程，查询结果的实例不会被删除。
举个例子，以下为请求处理模块收到创建查询任务的请求，在通过现有查询结果创建任务并填充响应数据时的代码：
Model::JobQueryResult::Use(query_id, [task_name,p_session,expire_time,&p_task](Model::JobQueryResult* result){
                        uint64_t version_after = result->get_version();
                        p_task = &Model::Task::Add(task_name, p_session->get_user(), result->get_query_expression_str(), version_after, expire_time);
                    })){
                        auto& task = *p_task;
                        auto* p_task_proto = response.add_task_list();

                        p_task_proto->set_id(task.get_id());
                        p_task_proto->set_username(task.get_user().get_name());
                        p_task_proto->set_query_expression(task.get_query_expression_str());
                        p_task_proto->set_taskname(task.get_task_name());
                        p_task_proto->set_expire_time(Common::DateHelper::ToString(task.get_expire_time()));
                        p_task_proto->set_query_result_count(task.get_query_result_count());

                        response.set_error_code(TencentJobHunterMessage::ErrorCode::SUCCESS);
                    }

将具体业务逻辑代码放进Use里，可以保证代码执行途中，查询结果的实例不会被删除。
Use()和Erase()的具体实现如下：
void JobQueryResult::Erase(const std::string& id)
{
    using Common::DBManager;
    using namespace Base::Database;

    JobQueryResult* p_result = GetPointer(id);


    if (p_result != nullptr)
    {


        //因为删除的是数组，所以要避免语句执行一半被中断而出现意外导致数据残缺的情况，要将整个过程当做是一个原子操作对待
        MultiSqlTask *task = new MultiSqlTask;

        static const std::string kSql1("DELETE FROM tjh_queryresult_item WHERE query_id = ?;");
        task->emplace(SqlTaskType::DML, kSql1, id);

        static const std::string kSql2("DELETE FROM tjh_queryresult WHERE query_id = ?;");
        task->emplace(SqlTaskType::DML, kSql2, id);

        Common::DBManager::EnqueueMultiTask(SqlTaskPriority::Low, task);

        //解决别处使用指针时，因为对象被销毁导致的野指针问题
        //这里使用条件变量，确保没有其他线程在用对象时才删除


        //静态容器锁上锁
        std::lock_guard database_lock(_mutex_database);
        //给需删除的实例上锁
        std::lock_guard instance_lock(p_result->m_mutex);

        p_result->m_user.set_query_result(nullptr, p_result->get_query_no());
        _result_map.erase(id);

    }
}
bool JobQueryResult::Use(const std::string& id, const std::function& func)
{    
    //静态容器锁上锁
    std::unique_lock database_lock(_mutex_database);

    auto it = _result_map.find(id);

    JobQueryResult* p = nullptr;
    if (it != _result_map.end())
    {
p = &it->second;
    }

    if (p == nullptr)return false;

    {        
        std::lock_guard instance_lock(p->m_mutex);//给需访问的实例上锁
        database_lock.unlock();//静态容器锁解锁
        func(p);
    }

    return true;
}

这里的同步使用了2个锁，一个锁用来保证_result_map（储存查询结果实例的静态容器）的线程安全，这个锁是静态锁。另一个用来保证实例本身的安全，为实例里的成员锁（防止使用实例指针的过程中实例被销毁）
其实只用一个静态锁也可以，但会造成一定程度的浪费，因为Use()在搜索完静态容器后得到实例的指针，开始执行内部方法时静态容器已经是安全的了，只是不能删除Use正在引用的实例而已。多引入一个实例锁的话，Use()在执行内部方法时其他方法就能访问静态容器了。
Add方法：
JobQueryResult& JobQueryResult::Add(User& user, int query_no,
    const std::string& query_expression_str, const std::vector& datas, uint64_t version)
{
    using Common::DBManager;
    using namespace Base::Database;

    std::string id = _GetRandomID();


    std::unique_lock lock(_mutex_database);

    JobQueryResult& result = _result_map.emplace(std::piecewise_construct, std::forward_as_tuple(id),
        std::forward_as_tuple(id, user, query_no, query_expression_str, version)).first->second;
    result.m_datas = datas;

    user.set_query_result(&result, query_no);

    lock.unlock();

    //以下为数据库操作

    //因为插入的是数组，所以要避免插入一半时出现意外导致数据残缺的情况，要将整个过程当做是一个原子操作对待
    MultiSqlTask *task = new MultiSqlTask;

    static const std::string kSql1("DELETE FROM tjh_queryresult_item WHERE query_id IN (SELECT query_id FROM tjh_queryresult WHERE user_name = ? AND query_no = ?);");
    task->emplace(SqlTaskType::DML, kSql1, user.get_name(), query_no);

    static const std::string kSql2("DELETE FROM tjh_queryresult WHERE user_name = ? AND query_no = ?;");
    task->emplace(SqlTaskType::DML, kSql2, user.get_name(), query_no);

    static const std::string kSql3("INSERT INTO tjh_queryresult(query_id,user_name,query_no,query_expression,version) VALUES(?,?,?,?,?);");
    task->emplace(SqlTaskType::DML, kSql3, id, user.get_name(), query_no, query_expression_str,version);

    static const std::string kSql4("INSERT INTO tjh_queryresult_item(query_id,job_id,result_index) VALUES(?,?,?);");
    for (int i = 0, length = datas.size(); i emplace(SqlTaskType::DML, kSql4, id, datas[i]->get_id(),i);
    }

    Common::DBManager::EnqueueMultiTask(SqlTaskPriority::Low, task);

    return result;
}

查询结果类的Add方法相比其他实体管理类区别在于数据库操作上面。由于查询结果会有多条记录，一条一条插入的话，如果作业进行到一半出现什么意外事故被中断，那么就会有数据残缺的情况。
因此要么做，要么不做，这里将整个数据库操作封装成一个事务，确保整个过程的原子性。
其他方法略。

logic/server/model/task.html.body:
任务类
任务是一种特殊的查询，如果说普通的查询操作是静态的、一次性的话，那么任务查询则是动态的、持续性的查询。
经过一次查询，用户并不一定从现在的结果中能找到想要，那么系统允许基于这个查询创建持续性的查询任务，每当出现新职位的时候都会进行增量查询，直到找到想要的结果为止。
所以任务类的结构和查询结果类其实比较相似，都会储存查询结果。不同的是查询结果类的结果是静态的，而任务类的查询结果可能随时会增加。
具体属性和方法如下：
实例属性：



属性名称
类型
描述




id
int
任务id


name
std::string
任务名称(可由用户自定义)


query_expression_str
std::string
查询表达式


job_version_after
uint64_t
只查询创建时间在这之后的的职位


expire_time
Base::DateTime
任务的失效时间（到达失效时间后，后台不再继续查询）


exist
bool
任务是否存在（进行删除时，会将这个属性标记为false。下次重启程序时将不加载exist==false的任务，来间接达到删除的目的。这样可以解决一些同步问题）


query_result
std::vector
查询结果


user
User&
所关联的用户的引用



实例方法（针对某个具体任务的操作）：



实例方法
描述




各属性的get方法
如get_id()、get_name()等等


int do_query()
对整个职位库范围进行查询，查询结果将自动添加到query_result中。返回值为所添加的查询结果的数目


int do_query(const std::vector& job_pointer_list)
和上面的方法不同点在于可以给定一个职位数组，在这个数组范围进行查询。返回值为所添加的查询结果的数目



do_query()代码：
int Task::do_query()
{
    if (!m_exist) return -1;

    std::vector query_result;
    uint64_t version = Model::Job::Query(get_expression(), query_result, m_job_version_after);

    add_query_result(query_result,version);
    if (query_result.size()>0 && m_exist) m_user.notify_task_query_result_changed();
    return query_result.size();
}

int Task::do_query(const std::vector& job_pointer_list)
{
    if (!m_exist) return -1;

    std::vector query_result;

    uint64_t version = m_job_version_after;


    for (const Job* p_job : job_pointer_list)
    {
        if (version get_version()) version = p_job->get_version();
        if (get_expression()->is_match(*p_job))
        {
            query_result.emplace_back(p_job);
        }
    }

    add_query_result(query_result,version);
    if (query_result.size()>0 && m_exist) m_user.notify_task_query_result_changed();
    return query_result.size();
}

void Task::add_query_result(const std::vector& query_result,uint64_t version)
{
    using namespace Base::Database;

    if (query_result.size() > 0)
    {
        //因为插入的是数组，所以要避免插入一半时出现意外导致数据残缺的情况，要将整个过程当做是一个原子操作对待
        MultiSqlTask *task = new MultiSqlTask;

        static const std::string kSql1("INSERT INTO tjh_task_queryresult_item(task_id,job_id,result_index) VALUES(?,?,?);");

        for (int i = 0, length = query_result.size(), offset = m_query_result.size(); i emplace(SqlTaskType::DML, kSql1, m_id, query_result[i]->get_id(), offset + i);
        }

        static const std::string kSql2("UPDATE tjh_task_detail SET job_version_after=? WHERE task_id=?;");
        task->emplace(SqlTaskType::DML, kSql2, version, m_id);

        Common::DBManager::EnqueueMultiTask(SqlTaskPriority::Low, task);

        //如果有查询结果，则追加到已有结果当中
        {
            std::unique_lock lock(m_mutex_data);
            m_query_result.insert(m_query_result.end(), query_result.begin(), query_result.end());
            //更新职位数据库版本，下次查询只查询在这个版本以后的职位
            m_job_version_after = version;
        }
    }
    else
    {
        std::unique_lock lock(m_mutex_data);
        //更新职位数据库版本，下次查询只查询在这个版本以后的职位
        m_job_version_after = version;
    }
}

